public class act2App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int N = 5;
        double A = 2.5;
        char C = 'a';
        double suma = N + A;
        double diferencia = A - N;
    
        System.out.println("N:"+ N + " A:"+ A + " C:"+ C);
        System.out.println("Suma de N + A = " + suma);
        System.out.println("Diferencia entre N - A = " + diferencia);
        System.out.println("Valor de " + C +" = "+ (int)C);
	}

}
